import React, { Component } from 'react';
import { FaCode, FaUsers, FaTools, FaLightbulb } from 'react-icons/fa';

class AboutMe extends Component {
  render() {
    const cardData = [
      {
        icon: <FaCode size={30} />,
        text: 'I’m a passionate QA Engineer with solid experience in automation, performance, and accessibility testing.',
      },
      {
        icon: <FaUsers size={30} />,
        text: 'I enjoy working closely with teams to deliver quality software that really works for users.',
      },
      {
        icon: <FaTools size={30} />,
        text: 'I lead testing efforts, help improve processes, and share knowledge to keep projects running smoothly.',
      },
      {
        icon: <FaLightbulb size={30} />,
        text: 'What motivates me is curiosity, the desire to solve problems, the constant grow and the belief that learning with my team is one of the best parts of this job.',
      },
    ];

    return (
      <section
        id="About"
        className="w-full px-6 py-16 bg-gradient-to-b from-transparent via-[#1e1e1e] to-black text-white"
      >
        <div className="max-w-4xl mx-auto space-y-12">
          <h2 className="text-4xl font-bold tracking-wide mb-6 text-white">About Me</h2>

          <div className="grid md:grid-cols-2 gap-8">
            {cardData.map((card, index) => (
              <div
                key={index}
                className="bg-white/5 backdrop-blur-md border border-white/10 rounded-2xl p-6 shadow-lg hover:shadow-xl transition duration-300 text-left"
              >
                <div className="flex items-start gap-4">
                  <div className="text-green-400 mt-1">{card.icon}</div>
                  <p className="text-base md:text-lg leading-relaxed text-white">
                    {card.text}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  }
}

export default AboutMe;
