import React, { Component } from 'react';
import { FaCode, FaUsers, FaTools, FaLightbulb } from 'react-icons/fa';

class AboutMe extends Component {
  render() {
    const cardData = [
      {
        icon: <FaCode size={28} className="text-green-400" />,
        text: 'I’m a passionate QA Engineer with solid experience in automation, performance, and accessibility testing.',
      },
      {
        icon: <FaUsers size={28} className="text-green-400" />,
        text: 'I enjoy working closely with teams to deliver quality software that really works for users.',
      },
      {
        icon: <FaTools size={28} className="text-green-400" />,
        text: 'I lead testing efforts, help improve processes, and share knowledge to keep projects running smoothly.',
      },
      {
        icon: <FaLightbulb size={28} className="text-green-400" />,
        text: 'What motivates me is curiosity, the desire to solve problems, the constant growth and the belief that learning with my team is one of the best parts of this job.',
      },
    ];

    return (
      <section
        id="About"
        className="w-full px-6 py-20 bg-black text-white"
      >
        <div className="max-w-5xl mx-auto">
          <h2 className="text-4xl font-bold mb-12 text-left">About Me</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {cardData.map((card, index) => (
              <div
                key={index}
                className="bg-[#1e1e1e] p-6 rounded-2xl shadow-lg hover:shadow-xl transition duration-300"
              >
                <div className="flex gap-4 items-start">
                  {card.icon}
                  <p className="text-lg leading-relaxed text-white text-left">
                    {card.text}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  }
}

export default AboutMe;
