import React, { Component } from 'react';

class Blog extends Component {
  state = {
    posts: [],
    currentIndex: 0,
  };

  componentDidMount() {
    this.fetchMediumPosts();
  }

  fetchMediumPosts = async () => {
    try {
      // Use o RSS JSON do Medium para seu usuário
      const response = await fetch(
        'https://api.rss2json.com/v1/api.json?rss_url=https://medium.com/feed/@viviannemelo'
      );
      const data = await response.json();
      if (data.status === 'ok') {
        this.setState({ posts: data.items.slice(0, 5) }); // pega os 5 mais recentes
      }
    } catch (error) {
      console.error('Erro ao buscar posts do Medium:', error);
    }
  };

  prevSlide = () => {
    this.setState((prevState) => ({
      currentIndex:
        prevState.currentIndex === 0
          ? prevState.posts.length - 1
          : prevState.currentIndex - 1,
    }));
  };

  nextSlide = () => {
    this.setState((prevState) => ({
      currentIndex:
        prevState.currentIndex === prevState.posts.length - 1
          ? 0
          : prevState.currentIndex + 1,
    }));
  };

  render() {
    const { posts, currentIndex } = this.state;

    if (posts.length === 0) return <p className="text-center py-6">Loading posts...</p>;

    return (
      <div className="MediumCarousel max-w-4xl mx-auto my-12 px-4">
        <h2 className="text-2xl font-semibold mb-6 text-center">Latest Blog Posts</h2>
        <div className="relative overflow-hidden rounded-lg shadow-lg">
          {posts.length > 0 && (
            <div className="carousel-slide flex transition-transform duration-500" style={{ transform: `translateX(-${currentIndex * 100}%)` }}>
              {posts.map((post) => (
                <a
                  key={post.guid}
                  href={post.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="carousel-item flex-shrink-0 w-full flex flex-col items-center bg-white p-4"
                >
                  <img
                    src={post.thumbnail || 'https://via.placeholder.com/400x200?text=No+Image'}
                    alt={post.title}
                    className="w-full h-48 object-cover rounded-md mb-4"
                    style={{ maxHeight: '200px' }}
                  />
                  <h3 className="text-lg font-semibold mb-2 text-center">{post.title}</h3>
                  <p className="text-sm text-gray-600 text-center line-clamp-3">{post.description.replace(/<[^>]+>/g, '')}</p>
                </a>
              ))}
            </div>
          )}

          {/* Navegação */}
          <button
            onClick={this.prevSlide}
            aria-label="Previous"
            className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-blue-600 text-white rounded-full p-2 hover:bg-blue-700 transition"
          >
            &#8592;
          </button>
          <button
            onClick={this.nextSlide}
            aria-label="Next"
            className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-blue-600 text-white rounded-full p-2 hover:bg-blue-700 transition"
          >
            &#8594;
          </button>
        </div>
      </div>
    );
  }
}

export default Blog;
