import React, { Component } from 'react';

class Blog extends Component {
  state = {
    posts: [],
    currentIndex: 0,
  };

  componentDidMount() {
    this.fetchMediumPosts();
  }

  fetchMediumPosts = async () => {
    try {
      const response = await fetch(
        'https://api.rss2json.com/v1/api.json?rss_url=https://medium.com/feed/@viviannemelo'
      );
      const data = await response.json();
      if (data.status === 'ok') {
        this.setState({ posts: data.items.slice(0, 7) }); // pegar mais para o carrossel
      }
    } catch (error) {
      console.error('Erro ao buscar posts do Medium:', error);
    }
  };

  prevSlide = () => {
    this.setState((prevState) => ({
      currentIndex:
        prevState.currentIndex === 0
          ? prevState.posts.length - 1
          : prevState.currentIndex - 1,
    }));
  };

  nextSlide = () => {
    this.setState((prevState) => ({
      currentIndex:
        prevState.currentIndex === prevState.posts.length - 1
          ? 0
          : prevState.currentIndex + 1,
    }));
  };

  // Função para cortar texto e adicionar reticências
  trimText = (text, maxLength = 100) => {
    if (!text) return '';
    const cleanText = text.replace(/<[^>]+>/g, ''); // remove tags html
    return cleanText.length > maxLength
      ? cleanText.substring(0, maxLength) + '...'
      : cleanText;
  };

  render() {
    const { posts, currentIndex } = this.state;

    if (posts.length === 0)
      return <p className="text-center py-6">Loading posts...</p>;

    // Para mostrar 3 posts lado a lado com destaque no meio,
    // pegamos o post atual, anterior e próximo.
    const total = posts.length;
    const prevIndex = currentIndex === 0 ? total - 1 : currentIndex - 1;
    const nextIndex = currentIndex === total - 1 ? 0 : currentIndex + 1;

    return (
      <div className="MediumCarousel max-w-5xl mx-auto my-12 px-4">
        <h2 className="text-3xl font-semibold mb-8 text-center">Latest Blog Posts</h2>

        <div className="relative flex justify-center items-center space-x-6">
          {/* Post anterior - menor e opaco */}
          <a
            href={posts[prevIndex].link}
            target="_blank"
            rel="noopener noreferrer"
            className="carousel-item flex-shrink-0 w-1/4 cursor-pointer opacity-60 hover:opacity-90 transition-opacity duration-300"
            style={{ maxWidth: '250px' }}
            aria-label={`Previous post: ${posts[prevIndex].title}`}
          >
            <img
              src={posts[prevIndex].thumbnail || 'https://via.placeholder.com/400x200?text=No+Image'}
              alt={posts[prevIndex].title}
              className="w-full h-40 object-cover rounded-lg shadow-md mb-2"
            />
            <h3 className="text-md font-semibold line-clamp-2">{posts[prevIndex].title}</h3>
          </a>

          {/* Post atual - maior, em destaque */}
          <a
            href={posts[currentIndex].link}
            target="_blank"
            rel="noopener noreferrer"
            className="carousel-item flex-shrink-0 w-1/2 cursor-pointer shadow-xl rounded-lg bg-white p-4"
            style={{ maxWidth: '500px' }}
            aria-label={`Current post: ${posts[currentIndex].title}`}
          >
            <img
              src={posts[currentIndex].thumbnail || 'https://via.placeholder.com/600x300?text=No+Image'}
              alt={posts[currentIndex].title}
              className="w-full h-56 object-cover rounded-lg shadow-lg mb-4"
            />
            <h3 className="text-xl font-bold mb-2">{posts[currentIndex].title}</h3>
            <p className="text-gray-700 text-sm leading-relaxed">
              {this.trimText(posts[currentIndex].description, 150)}
            </p>
          </a>

          {/* Próximo post - menor e opaco */}
          <a
            href={posts[nextIndex].link}
            target="_blank"
            rel="noopener noreferrer"
            className="carousel-item flex-shrink-0 w-1/4 cursor-pointer opacity-60 hover:opacity-90 transition-opacity duration-300"
            style={{ maxWidth: '250px' }}
            aria-label={`Next post: ${posts[nextIndex].title}`}
          >
            <img
              src={posts[nextIndex].thumbnail || 'https://via.placeholder.com/400x200?text=No+Image'}
              alt={posts[nextIndex].title}
              className="w-full h-40 object-cover rounded-lg shadow-md mb-2"
            />
            <h3 className="text-md font-semibold line-clamp-2">{posts[nextIndex].title}</h3>
          </a>
        </div>

        {/* Navegação */}
        <div className="flex justify-center mt-6 space-x-8">
          <button
            onClick={this.prevSlide}
            aria-label="Previous"
            className="bg-blue-600 text-white rounded-full p-3 hover:bg-blue-700 transition-shadow shadow-md"
          >
            &#8592;
          </button>
          <button
            onClick={this.nextSlide}
            aria-label="Next"
            className="bg-blue-600 text-white rounded-full p-3 hover:bg-blue-700 transition-shadow shadow-md"
          >
            &#8594;
          </button>
        </div>
      </div>
    );
  }
}

export default Blog;
