import React, { Component } from 'react';

class Blog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      current: 0,
    };
  }

  componentDidMount() {
    fetch('https://api.rss2json.com/v1/api.json?rss_url=https://medium.com/feed/@viviannemelo')
      .then(res => res.json())
      .then(data => {
        if (data.items) {
          const articles = data.items.map(post => ({
            ...post,
            thumbnail: this.extractImage(post.content),
          }));
          this.setState({ posts: articles });
        }
      })
      .catch(error => console.error('Erro ao carregar feed do Medium:', error));
  }

  extractImage(html) {
    const match = html.match(/<img[^>]+src="([^">]+)"/);
    return match ? match[1] : null;
  }

  prevSlide = () => {
    const { current, posts } = this.state;
    this.setState({ current: (current - 1 + posts.length) % posts.length });
  };

  nextSlide = () => {
    const { current, posts } = this.state;
    this.setState({ current: (current + 1) % posts.length });
  };

  goToSlide = (index) => {
    this.setState({ current: index });
  };

  render() {
    const { posts, current } = this.state;

    if (posts.length === 0) {
      return <p className="text-center">Carregando publicações...</p>;
    }

    const post = posts[current];

    return (
      <div className="relative max-w-4xl mx-auto mt-10 rounded-2xl overflow-hidden shadow-lg">
        {post.thumbnail && (
          <img
            src={post.thumbnail}
            alt="Capa do post"
            className="w-full h-64 object-cover"
          />
        )}

        <div className="p-6 bg-white">
          <h2 className="text-2xl font-bold mb-2 text-gray-800">{post.title}</h2>
          <p className="text-gray-600 line-clamp-3 mb-4">
            {post.description.replace(/<[^>]+>/g, '').slice(0, 200)}...
          </p>
          <a
            href={post.link}
            target="_blank"
            rel="noopener noreferrer"
            className="text-white bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded transition"
          >
            Ler no Medium →
          </a>
        </div>

        {/* Botões de navegação */}
        <button
          onClick={this.prevSlide}
          className="absolute top-1/2 left-4 -translate-y-1/2 bg-white/80 hover:bg-white p-2 rounded-full shadow"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="w-5 h-5 text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>

        <button
          onClick={this.nextSlide}
          className="absolute top-1/2 right-4 -translate-y-1/2 bg-white/80 hover:bg-white p-2 rounded-full shadow"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="w-5 h-5 text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
        </button>

        {/* Indicadores */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
          {posts.map((_, index) => (
            <button
              key={index}
              onClick={() => this.goToSlide(index)}
              className={`w-3 h-3 rounded-full ${
                index === current ? 'bg-blue-600' : 'bg-gray-300'
              }`}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default Blog;
