import React, { Component } from 'react';
import { FaCode, FaUsers, FaTools, FaLightbulb } from 'react-icons/fa';

class AboutMe extends Component {
  render() {
    return (
      <div className="AboutMe bg-white text-gray-800 px-6 py-12" id="About">
        <div className="about-text max-w-5xl mx-auto text-center">
          <h2 className="text-3xl font-bold mb-6">About Me</h2>

          <p className="mb-4 flex items-center justify-center gap-4 text-lg">
            <FaCode className="text-green-600 mr-3" />
            Iâ€™m a passionate QA Engineer with solid experience in automation,
            performance, and accessibility testing.
          </p>

          <p className="mb-4 flex items-center justify-center gap-4 text-lg">
            <FaUsers className="text-blue-600 mr-3"/>
            I enjoy working closely with teams to deliver quality software that really works for users.
          </p>

          <p className="mb-4 flex items-center justify-center gap-4 text-lg">
            <FaTools className="text-yellow-600 mr-3" />
            I lead testing efforts, help improve processes, and share knowledge to keep projects running smoothly.
          </p>

          <p className="flex items-center justify-center gap-4 text-lg">
            <FaLightbulb className="text-pink-600 mr-300" />
            What motivates me is curiosity, the desire to solve problems, the constant growth and the belief that learning from others is one of the best parts of this job.
          </p>
        </div>
      </div>
    );
  }
}

export default AboutMe;
