import React, { Component } from 'react';

class AboutMe extends Component {
  render() {
    const containerStyle = {
        maxWidth: '800px',
        margin: 'auto 12rem', // <-- 5rem de espaço acima
        textAlign: 'center',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    };
    return (
    <div style={containerStyle}>
      <div className="AboutMe bg-white text-gray-800 px-6 py-12" id="About">
        <div className="about-text max-w-5xl mx-auto text-center">
          <h2 className="text-3xl font-bold mb-6">About Me</h2>
          <p className="mb-4">
            I’m a passionate QA Engineer with solid experience in automation,
            performance, and accessibility testing. I enjoy working closely
            with teams to deliver quality software that really works for users.
          </p>
          <p className="mb-4">
            I lead testing efforts, help improve processes, and share knowledge
            to keep projects running smoothly.
          </p>
          <p>
            What motivates me is curiosity, the desire to solve problems, the
            constant grow and the belief that learning from others is one of
            the best parts of this job.
          </p>
        </div>
      </div>
    </div>
    );
  }
}

export default AboutMe;
