import React, { Component } from 'react';
import { FaCode, FaUsers, FaTools, FaLightbulb } from 'react-icons/fa';

class AboutMe extends Component {
  render() {
    const cardData = [
      {
        icon: <FaCode size={24} className="text-green-400" />,
        text: 'I’m a passionate QA Engineer with solid experience in automation, performance, and accessibility testing.',
      },
      {
        icon: <FaUsers size={24} className="text-green-400" />,
        text: 'I enjoy working closely with teams to deliver quality software that really works for users.',
      },
      {
        icon: <FaTools size={24} className="text-green-400" />,
        text: 'I lead testing efforts, help improve processes, and share knowledge to keep projects running smoothly.',
      },
      {
        icon: <FaLightbulb size={24} className="text-green-400" />,
        text: 'What motivates me is curiosity, the desire to solve problems, the constant growth, and the belief that learning with my team is one of the best parts of this job.',
      },
    ];

    return (
      <section id="About" className="bg-black py-16 px-6 text-white">
        <div className="max-w-5xl mx-auto">
          <h2 className="text-4xl font-bold mb-10 text-left">About Me</h2>

          <div className="space-y-6">
            {cardData.map((card, index) => (
              <div
                key={index}
                className="bg-[#1e1e1e] rounded-xl p-6 shadow-lg flex items-start gap-4 hover:shadow-2xl transition"
              >
                {card.icon}
                <p className="text-lg leading-relaxed text-left">{card.text}</p>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  }
}

export default AboutMe;
