import React, { Component } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';

class Blog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      current: 0,
    };
  }

  componentDidMount() {
    fetch('https://api.rss2json.com/v1/api.json?rss_url=https://medium.com/feed/@viviannemelo')
      .then(res => res.json())
      .then(data => {
        if (data.items) {
          this.setState({ posts: data.items });
        }
      })
      .catch(error => console.error('Erro ao carregar feed do Medium:', error));
  }

  prevSlide = () => {
    const { current, posts } = this.state;
    this.setState({ current: (current - 1 + posts.length) % posts.length });
  };

  nextSlide = () => {
    const { current, posts } = this.state;
    this.setState({ current: (current + 1) % posts.length });
  };

  goToSlide = (index) => {
    this.setState({ current: index });
  };

  render() {
    const { posts, current } = this.state;

    if (posts.length === 0) {
      return <p className="text-center">Carregando posts do Medium...</p>;
    }

    const post = posts[current];

    return (
      <div className="relative w-full max-w-3xl mx-auto overflow-hidden">
        <div className="p-6 bg-white rounded-xl shadow h-64">
          <h2 className="text-xl font-bold mb-2">{post.title}</h2>
          <p className="text-gray-600 line-clamp-3">
            {post.description.replace(/<[^>]+>/g, '').slice(0, 200)}...
          </p>
          <a href={post.link} target="_blank" rel="noopener noreferrer" className="text-blue-500 mt-4 inline-block">
            Ler no Medium â†’
          </a>
        </div>

        <button onClick={this.prevSlide} className="absolute top-1/2 left-2 -translate-y-1/2 p-2 bg-white shadow rounded-full hover:bg-gray-100">
          <ChevronLeft />
        </button>
        <button onClick={this.nextSlide} className="absolute top-1/2 right-2 -translate-y-1/2 p-2 bg-white shadow rounded-full hover:bg-gray-100">
          <ChevronRight />
        </button>

        <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 flex gap-2">
          {posts.map((_, index) => (
            <button
              key={index}
              onClick={() => this.goToSlide(index)}
              className={`w-3 h-3 rounded-full ${index === current ? 'bg-blue-600' : 'bg-gray-300'}`}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default Blog;
